mapper.select:
SELECT *
FROM chat_room c , cur_user_info u
WHERE c.boardnumber=u.boardnumber AND c.boardnumber = #{boardnumber}

mapper.update:
UPDATE chat_room
SET room_name= #{roomName} AND max_member=#{roomName} AND secret=#{secret} AND room_password=#{roomPassword}
where boardnumber=#{boardnumber}

UPDATE cur_room_info
SET room_name= #{roomName} AND secret=#{secret}
where boardnumber=#{boardnumber}
